                    </ExtCheckBoxField>
                    <ExtCheckBoxField [boxLabel]='"Checked"' [checked]="true">
                    </ExtCheckBoxField>
                    <ExtCheckBoxField [boxLabel]='"Disabled"' [disabled]="true">
                    </ExtCheckBoxField>
                    <ExtCheckBoxField [boxLabel]='"Disabled (checked)"'
                       [disabled]="true" [checked]="true">
                    </ExtCheckBoxField>
