 import { ExtContainer, ExtFormPanel, ExtDatePickerField } from '@sencha/ext-react';

 export default class MyExample extends Component {
     render() {
         return (
             <ExtContainer layout="center">
                 <ExtFormPanel shadow>
                     <ExtDatePickerField
                         width={150}
                         value={new Date()}
                         destroyPickerOnHide
                         label="Date"
                         picker={{
                             yearFrom: 1990
                         }}
                     />
                 </ExtFormPanel>
